function cir = get_cir_pic_from_file(fpath, name)
    csi_trace=read_bf_file(fpath);
    csi_trace = get_csi_trace_sqeezed(csi_trace);
    cir=get_cir_pic_from_csitrace(csi_trace, name);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
end
